import React, { useState } from 'react';
import axios from 'axios';
import ButtonAdd from './components/ButtonAdd';
import NavLink from './components/NavLink';
import Card from './components/card'; // Corrigé le chemin pour correspondre à la convention de nommage

const App = () => {
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(false);

    const fetchMessage = () => {
        setLoading(true);
        axios.get('http://localhost:5216/api/example/hello') // Mettez à jour l'URL
            .then(response => {
                setData(response.data);
            })
            .catch(error => {
                console.error('Erreur lors de la récupération des données:', error);
            })
            .finally(() => {
                setLoading(false);
            });
    };

    // Données pour les cartes
    const cardsData = [
        {
            img: 'https://www.powertrafic.fr/wp-content/uploads/2023/04/image-ia-exemple.png',
            title: 'Test',
            description: 'Ceci est une description de test.',
        },
        // Tu peux ajouter d'autres cartes ici
    ];

    return (
        <div>
            <NavLink /> {/* Affichage de la navigation */}
            <ButtonAdd 
                onClick={fetchMessage} 
                label={'Import'} 
                disabled={loading} 
            />
            {loading && <p>Chargement...</p>} {/* Affiche un message pendant le chargement */}
            {data && <p>Données: {data}</p>} {/* Affiche les données récupérées */}

            {/* Affichage des cartes */}
            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                {cardsData.map((card, index) => (
                    <Card 
                        key={index} 
                        imageSrc={card.img} 
                        title={card.title} 
                        description={card.description} 
                    />
                ))}
            </div>
        </div>
    );
};

export default App;
