import React, { useState } from 'react'; 
import styles from './card.module.css'; // Importation du module CSS

const Card = ({ imageSrc, title, description }) => {
    const [isHovered, setIsHovered] = useState(false); // État pour le survol

    return (
        <div 
            className={styles.card}
            onMouseEnter={() => setIsHovered(true)}  // Événement de survol
            onMouseLeave={() => setIsHovered(false)} // Événement de sortie du survol
        >
            <img src={imageSrc} alt={title} className={styles.cardImage} />
            <div className={styles.cardContent}>
                <h2 className={styles.cardTitle}>{title}</h2>
                <p className={styles.cardDescription}>{description}</p>
                {isHovered && <button className={styles.button}>Download</button>} {/* Affichage conditionnel du bouton */}
            </div>
        </div>
    );
};

export default Card;
