import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ButtonAdd from './components/ButtonAdd';
import NavLink from './components/NavLink';
import Card from './components/card';
import cardsData from './cardsData'; // Importer le fichier contenant les données

const App = () => {
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(false);
    const [navVisible, setNavVisible] = useState(false); // État pour la visibilité de la barre de navigation

    const fetchMessage = () => {
        setLoading(true);
        axios.get('http://localhost:5216/api/example/hello')
            .then(response => {
                setData(response.data);
            })
            .catch(error => {
                console.error('Erreur lors de la récupération des données:', error);
            })
            .finally(() => {
                setLoading(false);
            });
    };

    const handleMouseMove = (event) => {
        // Vérifiez si la souris est dans la zone de la navigation
        if (event.clientX < 100) { // 100 pixels depuis la gauche
            setNavVisible(true);
        } else {
            setNavVisible(false);
        }
    };

    useEffect(() => {
        window.addEventListener('mousemove', handleMouseMove);

        // Nettoyer l'écouteur d'événements lorsque le composant est démonté
        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);

    return (
        <div>
            <div className={`nav ${navVisible ? 'visible' : ''}`}>
                <NavLink /> {/* Affichage de la navigation */}
            </div>

            {loading && <p>Chargement...</p>} {/* Affiche un message pendant le chargement */}
            {data && <p>Données: {data}</p>} {/* Affiche les données récupérées */}

            {/* Affichage des cartes */}
            <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
                {cardsData.map((card, index) => (
                    <Card 
                        key={index} 
                        imageSrc={card.img} // Utiliser imageSrc
                        compressfile={card.compressfile} // Passer compressfile
                    />
                ))}
            </div>
        </div>
    );
};

export default App;
