import React, { useState } from 'react'; // Assurez-vous d'importer useState ici
import styles from './card.module.css';
import MyButton from './BtnDL';


const Card = ({ imageSrc}) => {
    const [isHovered, setIsHovered] = useState(false);
    const Downloading = ()=>{
     // Créer un lien temporaire pour le téléchargement
     const link = document.createElement('a');
     link.href = imageSrc; // Spécifiez l'URL de l'image
     link.setAttribute('download', image-ia-exemple.png || 'downloaded-image.png'); // Nom du fichier téléchargé
     document.body.appendChild(link);
     link.click(); // Simuler le clic pour télécharger
     document.body.removeChild(link); // Nettoyer le lien
    }

    return (
        <div 
        className={styles.card}
        onMouseEnter={()=> setIsHovered(true)}
        onMouseLeave={()=> setIsHovered(false)}
        > {}
            <img src={imageSrc} className={styles.cardImage} /> {}
            {isHovered === true &&(
                     <div className={styles.cardContent}> 
                     <MyButton
                        label = "Download"
                        onClick={Downloading}
                     />


                 </div>
                )}
        </div>

    );
};

export default Card;
